def get_webpage_data(url):
    """
    Extrai o conte√∫do de uma URL espec√≠fica de newsletter
    """
    try:
        print(f"  ‚Üí Fazendo requisi√ß√£o para: {url}")
        response = requests.get(url, timeout=30)  # Timeout de 30 segundos
        response.raise_for_status()
        print(f"  ‚Üí Resposta recebida: {response.status_code}")
        
        soup = BeautifulSoup(response.text, "html.parser")
        
        # Extrai textos de <span>, <p> e <div> e concatena tudo
        all_texts = []
        seen_texts = set()
        for tag in soup.find_all(["span", "p", "div"]):
            text = tag.get_text(separator=' ', strip=True)
            if text and text not in seen_texts:
                seen_texts.add(text)
                all_texts.append(text)
        
        # Junta todos os textos em um √∫nico documento
        combined_text = " ".join(all_texts)
        combined_text = ' '.join(combined_text.split())
        
        # TESTE: Enviar apenas os primeiros 300 caracteres
        test_content = combined_text[:300] if len(combined_text) > 300 else combined_text
        
        documents = []
        if combined_text:
            documents.append({
                "fields": {
                    "title": url,
                     "content": combined_text,  # COMENTADO: conte√∫do completo
                   # "content": test_content,    # TESTE: apenas primeiros 300 caracteres
                    "id": url,
                    "url": url
                }
            })
            print(f"  ‚Üí Conte√∫do extra√≠do: {len(combined_text)} caracteres total")
        else:
            print(f"  ‚Üí Nenhum conte√∫do encontrado")
        
        return documents
        
    except requests.exceptions.RequestException as e:
        print(f"  ‚ùå Erro ao acessar URL: {e}")
        return []
    except Exception as e:
        print(f"  ‚ùå Erro inesperado: {e}")
        return []

def main():
    try:
        chatVoltDatas = chatvolt.getAll()
        chatVoltDataSources = chatVoltDatas.get("datasources", {})

        # Obt√©m todos os links de newsletters
        newsletter_links = get_newsletter_links()
        print(f"Encontrados {len(newsletter_links)} links de newsletters para processar")
        
        success_count = 0
        error_count = 0
        
        for i, url in enumerate(newsletter_links, 1):
            print(f"\n[{i}/{len(newsletter_links)}] Processando: {url}")
            
            try:
                # Extrai o conte√∫do de cada newsletter
                webpageDatas = get_webpage_data(url)
                
                if not webpageDatas:
                    print(f"  ‚ö†Ô∏è Nenhum dado extra√≠do para: {url}")
                    error_count += 1
                    continue
                
                for webpageData in webpageDatas:
                    # Adiciona o mbtype para garantir o envio correto
                    webpageFields = dict(webpageData['fields'])
                    webpageFields['mbtype'] = 'webpage'
                  
                    print(f"  ‚Üí Enviando para ChatVolt...")
                    chatvolt.sendWebpage(webpageFields)
                    print(f"  ‚úÖ Newsletter enviada com sucesso: {url}")
                    success_count += 1
                    
                # Delay de 2 segundos entre cada URL para evitar rate limiting
                print(f"  ‚è≥ Aguardando 2 segundos antes da pr√≥xima requisi√ß√£o...")
                time.sleep(2)
                      
            except Exception as e:
                print(f"  ‚ùå Erro ao processar {url}: {e}")
                error_count += 1
                # Delay menor em caso de erro
                time.sleep(1)
                continue

        print(f"\nüìä Resumo:")
        print(f"  ‚úÖ Sucessos: {success_count}")
        print(f"  ‚ùå Erros: {error_count}")
            
    except Exception as e:
        print(f"‚ùå Erro geral no main: {e}")

if __name__ == '__main__':
    main()